import { useRouter } from "next/router";
import useSWR from "swr";
import fetcher from "../../lib/fetcher";
import Header from "../../components/header";
import Genres from "../../components/genres";

import Axios from "axios";

function Artist(props) {
  let headingStyle = {
    textAlign: "center",
    textDecoration: "underline",
    marginTop: "10px",
    fontSize: "25px"
  };
  //   console.log(props.data);

  function mapGenres(genres) {
    var html = ``;
    genres.map(genre => (html += `<p>${genre}</p>`));
    return html;
  }

  return (
    <>
      <Header header={{ title: props.data.name }} />

      {/* <div>{JSON.stringify(props)}</div> */}
      <h1 style={headingStyle}>Spotify Genres: </h1>
      <Genres genres={{ list: props.data.genres }} />
    </>
  );
}

Artist.getInitialProps = async function(context) {
  console.log(context);

  const { data } = await Axios.get(
    "http://localhost:3000/api/" + context.query
  );
  console.log(`got props`);

  return {
    data
  };
};

export default Artist;
